vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec2 scale = vec2(10.0, 10.0);

    // Base noise pattern with animation
    float n1 = noise(uv * scale + time);

    // Add higher frequency detail
    float n2 = noise(uv * scale * 3.0 + time * 2.0);
    float finalNoise = mix(n1, n2, 0.5);

    // Create color variations using HSL
    vec3 color;
    color.x = fract(time * 0.2 + uv.x * 2.0) * PI; // Hue variation
    color.y = 0.7 + 0.3 * sin(uv.y * scale.y + time); // Saturation
    color.z = 0.5 + 0.5 * finalNoise; // Lightness

    vec3 rgbColor = hsl2rgb(color);

    // Add movement to the pattern
    float morphScale = 1.0 + 0.5 * sin(time) * cos(uv.x * PI);
    vec2 distortedUV = uv * morphScale;

    // Create rotating pattern
    float angle = time;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle),
                         sin(angle), cos(angle));
    vec2 rotatedUV = distortedUV * rotMatrix;

    return vec4(rgbColor, 0.8);
}
