vec4 Effect2(vec2 uv) {
    // Create rotating coordinate system
    vec2 st = uv - 0.5;
    float angle = sin(iTime * 0.5) * 0.5 + 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 pos = rot * st * 1.5;
    // Generate turbulent noise pattern
    float t = turbulent(pos * 4.0 + iTime) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        (t * 0.6 + 0.2) * sin(iTime * 0.7 + pos.x * 4.0),
        0.8,
        0.5
    );

    // Add noise variation to color
    float noiseVar = noise(vec3(pos * 2.0, iTime)) * 0.1;
    hslColor.r += noiseVar;

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
