vec4 Effect2(vec2 uv) {
    // Create time-based animation
    float t = iTime * 0.5;

    // Compute multiple sine waves with different frequencies and amplitudes
    float wave1 = sin(uv.x * 8.0 + t) * 0.5;
    float wave2 = sin(uv.y * 4.0 - t) * 0.3;
    float wave3 = sin((uv.x + uv.y) * 6.0 + t * 2.0) * 0.2;

    // Combine waves and add turbulence
    float combined = (wave1 + wave2 + wave3) * 0.5;
    combined += turbulent(uv * 4.0 + vec2(t)) * 0.3;

    // Create color transitions using HSL
    vec3 color = hsl(
        sin(uv.x * 2.0 + t) * 180.0,   // Hue variation based on position and time
        0.7,                             // Saturation
        combined * 0.5 + 0.5             // Lightness modulation
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
