vec4 Effect2(vec2 uv) {
    // Create flowing wave patterns using sine functions combined with time
    float waveX = sin(uv.x * 10.0 + iTime) * 0.5;
    float waveY = cos(uv.y * 10.0 + iTime) * 0.5;

    // Combine waves and add turbulence for more dynamic movement
    float flow = sin(waveX + waveY + turbulent(vec2(uv.x * 5.0, uv.y * 5.0)));

    // Create rainbow effect using HSL colors
    vec3 color = hsl(flow * pi + iTime, 1.0, 0.5);

    return vec4(color, 1.0);
}
