vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to create a grid pattern
    vec2 gridUV = uv * 10.0;

    // Get cell position in grid
    vec2 cellPos = mod(gridUV, 1.0);

    // Calculate pulsing effect using time and sine function
    float pulse = sin(iTime * 0.5 + cellPos.x * 3.1415926) * 0.5 + 0.5;

    // Create color based on mouse position and grid pattern
    vec3 baseColor = hsl2rgb(vec3(pulse, 0.7, 0.5));

    // Add noise to create more dynamic effect
    float noiseValue = noise(cellPos * 10.0 + iTime);

    // Combine colors and noise for final output
    vec3 finalColor = baseColor * (0.5 + noiseValue * 0.5);

    return vec4(finalColor, 1.0);
}
