vec4 Effect2(vec2 uv) {
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create rotating color pattern using time-based sine waves
    theta += iTime * 0.5;

    // Create a color value based on polar coordinates and time
    vec3 color = vec3(
        sin(r * 10.0 + iTime) * 0.5 + 0.5,
        sin(r * 20.0 - theta) * 0.5 + 0.5,
        sin(theta * 5.0 + iTime) * 0.5 + 0.5
    );

    // Convert to HSL and back for a more vibrant effect
    vec3 hslColor = hsl2rgb(vec3(
        color.r * 360.0,
        0.7,
        0.5 + sin(iTime) * 0.3
    ));

    return vec4(hslColor, 1.0);
}
