vec4 Effect2(vec2 uv) {
    vec3 result;

    // Create a base color using sine function that shifts over time
    vec3 baseColor = sin(uv.xxy * 5.0 + iTime);

    // Add noise and turbulence to create dynamic variations
    float noiseValue = noise(uv * 10.0 + vec2(iTime, 0.0));
    float turbulentValue = turbulent(uv * 20.0 + vec2(iTime * 2.0, 0.0));

    // Combine base color with noise and turbulence
    result = baseColor * (noiseValue * 0.5 + 0.5) + turbulentValue * 0.3;

    // Add position-dependent rotation to create movement
    float angle = iTime * 0.1 + uv.x * 2.0;
    vec2 rotatedUV = uv * vec2(cos(angle), sin(angle));

    // Mix in additional noise for complexity
    result += noise(rotatedUV * 50.0) * 0.3;

    return vec4(result, 1.0);
}
