vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 centered = uv - 0.5;
    float r = length(centered);
    float theta = atan2(centered.y, centered.x);
    // Create pulsating effect using sine waves
    r += sin(r * 4.0 + iTime) * 0.3;
    theta += sin(theta * 3.0 + iTime) * 0.5;
    // Add noise for more complex patterns
    float noiseValue = turbulent(vec2(r, theta)) * 0.2;
    r += noiseValue;

    // Create color shifting effect
    vec3 colorHSL = vec3(
        sin(iTime * 2.5 + r * 4.0) * 0.5 + 0.5,  // Hue
        0.7,                                      // Saturation
        0.5 + noiseValue * 0.2                     // Lightness
    );

    vec3 color = hsl2rgb(colorHSL);

    return vec4(color, 1.0);
}
