vec4 Effect2(vec2 uv) {
    // Create base color using HSL
    vec3 color = hsl(0.5, 1.0, 0.5);

    // Add time-based variation
    float timeFactor = sin(iTime * 0.5) * 0.5 + 0.5;

    // Create position-dependent pattern using noise
    vec2 st = uv * 4.0; // Scale coordinates for more detail
    st += iTime; // Add time offset

    float n1 = noise(st);
    float n2 = turbulent(vec3(uv, iTime));

    // Combine noise values with time factor
    float shiftFactor = (n1 + n2 * 0.5) * timeFactor;

    // Create color shift effect by modifying hue
    vec3 shiftedColor = hsl2rgb(vec3(
        mod(hsl(0.5, 1.0, 0.5).x + shiftFactor * 2.0 * PI + iTime * 2.0 * PI, 1.0),
        1.0,
        0.5
    ));

    return vec4(shiftedColor, 1.0);
}
