vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for hexagonal symmetry
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating hexagonal pattern
    theta -= iTime * 0.5; // Rotate over time
    float petal = abs(sin(theta * 3.0)); // Hexagon shape

    // Add turbulence for glow effect
    vec3 noisePos = vec3(uv * 10.0, iTime);
    float glow = turbulent(noisePos) + turbulent(vec3(uv * 5.0, iTime));

    // Combine hexagonal pattern with glow
    float shape = (petal * 0.5 + 0.5) * r;
    shape = mix(shape, 1.0 - shape, 0.2); // Add inner glow

    // Dynamic color changes using HSL
    vec3 hslColor = vec3(theta * 0.5 + iTime, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Final color with glow overlay
    vec3 finalColor = mix(rgbColor, vec3(1.0), shape * (1.0 + glow * 0.5));

    return vec4(finalColor, 1.0);
}
