vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec3 color = vec3(0.0);

    // Create orbit traps using circles and lines
    vec2 pos1 = uv - vec2(sin(time)*0.3, cos(time)*0.3);
    vec2 pos2 = uv + vec2(sin(time)*0.3, cos(time)*0.3);
    vec2 center = uv;

    // Add multiple traps with different sizes and colors
    float d1 = length(pos1) < 0.4 ? 1.0 : 0.0;
    float d2 = length(pos2) < 0.4 ? 1.0 : 0.0;
    float d3 = abs(uv.x * 0.5 + uv.y) < 0.2 ? 1.0 : 0.0;

    // Accumulate colors from different traps
    color += hsl(0.1, 0.8, 0.6) * d1;
    color += hsl(0.3, 0.7, 0.5) * d2;
    color += hsl(0.5, 0.9, 0.4) * d3;

    // Add some turbulence for dynamic effect
    float turb = turbulent(uv + vec2(time)) * 0.1;
    color *= 1.0 + turb;

    return vec4(hsl2rgb(color), 1.0);
}
