vec4 Effect2(vec2 uv) {
    // Create base sine wave patterns with different frequencies
    float wave1 = sin(uv.x * 4.0 + iTime) * 0.5;
    float wave2 = sin(uv.y * 3.0 + iTime * 0.5) * 0.3;

    // Add noise and turbulence for organic movement
    vec2 noiseScale = uv * 100.0;
    float noiseVal = noise(noiseScale);
    float turbulentVal = turbulent(noiseScale);

    // Combine patterns with varying frequencies and phases
    float pattern = mix(wave1, wave2, 0.5) + (noiseVal + turbulentVal) * 0.3;

    // Create flowing color transitions using HSL
    vec3 color = hsl(
        iTime * 0.1 + uv.x * 2.0,
        0.7 + pattern * 0.3,
        0.5 + sin(iTime) * 0.4
    );

    // Add glowing effect using distance from center and time
    float glow = length(uv - vec2(0.5)) * 2.0;
    glow = smoothstep(1.0, 0.0, glow);
    color *= (pattern + glow) * 1.5;

    return vec4(hsl2rgb(color), 1.0);
}
