vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x) + iTime * 0.5;

    // Create multiple rotating wheels
    const int numWheels = 4;
    vec3 color = vec3(0.0);

    for (int i = -1; i <= 1; i += 2) {
        for (int j = -1; j <= 1; j += 2) {
            // Offset coordinates for each wheel
            vec2 offset = vec2(float(i), float(j)) * 0.5;
            vec2 coords = uv + offset;

            // Polar coordinates for each wheel
            radius = length(coords);
            angle = atan2(coords.y, coords.x) + iTime * float(i * j);

            // Pulsating effect based on time and radius
            float pulse = sin(iTime + radius * 5.0) * 0.5 + 0.5;
            radius *= pulse;

            // Color based on angle and radius
            vec3 hslColor = hsl(angle * 2.0, 1.0 - radius * 0.8, 0.6);
            vec3 rgbColor = hsl2rgb(hslColor);

            // Add turbulence for extra detail
            float noiseVal = turbulent(vec3(coords * 5.0, iTime));
            rgbColor *= 1.0 + noiseVal * 0.3;

            // Combine colors from all wheels
            color += rgbColor / float(numWheels);
        }
    }

    return vec4(color, 1.0);
}
