vec4 Effect2(vec2 uv) {
    // Create rotating sine wave patterns based on time
    float time = iTime * 0.5;

    // Calculate base color values using sine waves
    vec3 baseColor = vec3(
        sin(time + uv.x * 10.0),
        cos(time + uv.y * 10.0),
        sin(time + uv.x * 5.0 + uv.y * 5.0)
    );

    // Mix in noise for extra texture
    float noiseValue = noise(uv * 20.0 + vec2(sin(time), cos(time)));
    baseColor = mix(baseColor, vec3(noiseValue), 0.3);

    // Convert to HSL and back to RGB for vibrant colors
    vec3 hslColor = vec3(
        sin(time) * 0.5 + 0.5,
        1.0,
        0.6 + noiseValue * 0.2
    );
    vec3 finalColor = hsl2rgb(hslColor);

    return vec4(finalColor, 1.0);
}
