vec4 Effect2(vec2 uv) {
    // Create grid lines with pulsating effect
    float time = iTime * 0.5;

    // Horizontal line intensity
    float hline = sin(uv.x * pi + time) * 0.5 + 0.5;
    hline = smoothstep(0.4, 0.6, hline);

    // Vertical line intensity
    float vline = sin(uv.y * pi + time + PI/2.0) * 0.5 + 0.5;
    vline = smoothstep(0.4, 0.6, vline);

    // Combine lines and add pulsation
    float intensity = hline + vline;
    intensity = sin(intensity * pi + time * 2.0) * 0.5 + 0.5;

    // Add noise for randomness
    vec2 noiseUV = uv * 10.0 + time;
    float n = noise(noiseUV);
    intensity += n * 0.3;

    // Color shifting over time
    vec3 color = hsl(intensity * 360.0, 0.8, 0.5);
    color = hsl2rgb(color);

    // Final color with pulsation effect
    color *= sin(time) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
