vec4 Effect2(vec2 uv) {
    // Create base HSL color with position-dependent hue shift
    vec3 hslColor = vec3(0.6 + sin(uv.x * 10.0 + iTime) * 0.5, 1.0, 0.5);

    // Add time-based movement to the color wave
    float timeFactor = iTime * 0.2;

    // Use turbulence to create a flowing pattern
    float flow = turbulent(uv * 2.0 + vec2(timeFactor));

    // Combine position and time for final hue shift
    hslColor.r += uv.y * 0.5 + flow * 0.1;

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
