vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 center = uv - 0.5;
    float r = length(center);
    float theta = atan2(center.y, center.x) + iTime * 2.0 * PI;

    // Create color wheel effect using HSL colors
    vec3 hslColor = vec3(theta / (2.0 * PI), 1.0, 0.5 + 0.5 * r);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add some noise and turbulence for dynamic effect
    float noiseValue = turbulent(uv * 4.0) * 0.1;
    r += noiseValue;
    theta += noiseValue * 2.0 * PI;

    // Final color with alpha
    return vec4(rgbColor, 1.0);
}
