vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float radius = length(uv);
    float angle = atan2(uv.y, uv.x);

    // Create pulsating effect using time and sine function
    float pulse = sin(iTime + radius * 5.0) * 0.5 + 0.5;

    // Add turbulence to create more dynamic rings
    float ringValue = turbulent(vec2(radius * 10.0, angle));
    ringValue = mod(ringValue, 0.5);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        angle / (2.0 * PI) + pulse * 0.5,
        0.7,
        0.5 + ringValue * 0.5
    );

    // Convert to RGB and return as fragment color
    return vec4(hsl2rgb(hslColor), 1.0);
}
