vec4 Effect2(vec2 uv) {
    // Animate pattern over time
    float time = iTime * 0.5;

    // Create rotating UV coordinates
    vec2 st = uv * 10.0 - 5.0;
    st.x *= 16.0 / 9.0; // Maintain aspect ratio

    // Add rotation based on time
    float angle = sin(time) * 4.0;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedUV = st * rot;

    // Generate base pattern using turbulence
    float t1 = turbulent(rotatedUV + time);
    float t2 = turbulent((rotatedUV * 0.5) + time * 0.3);

    // Create color transitions
    vec3 color1 = hsl2rgb(vec3(time * 0.1, 0.7, 0.5));
    vec3 color2 = hsl2rgb(vec3(time * 0.1 + 0.5, 0.7, 0.5));

    // Combine patterns and colors
    vec3 finalColor = mix(color1, color2, t1) * (t2 * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
