vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a 3D grid of points
    for(float x = -10.0; x <= 10.0; x++) {
        for(float y = -10.0; y <= 10.0; y++) {
            for(float z = -10.0; z <= 10.0; z++) {
                // Rotate points over time
                float angle = iTime * 0.5;
                vec3 point = vec3(
                    x * cos(angle) - z * sin(angle),
                    y,
                    x * sin(angle) + z * cos(angle)
                );

                // Project to 2D and calculate distance from UV
                vec2 pos = (point.xy / (point.z * 0.5 + 1.0)) * 0.5 + 0.5;
                float dist = length(uv - pos);

                // Create glowing dots with color shifting
                if(dist < 0.05) {
                    vec3 hslColor = vec3(
                        (x + y + z + iTime) * 0.1,
                        0.7,
                        0.8
                    );
                    vec3 rgbColor = hsl2rgb(hslColor);

                    // Add depth variation
                    float intensity = 1.0 - abs(point.z) * 0.1;
                    color += rgbColor * (1.0 - dist * 20.0) * intensity;
                }
            }
        }
    }

    return vec4(color, 1.0);
}
