vec4 Effect2(vec2 uv) {
    // Create a grid of points
    vec2 st = floor(uv * 50.0);

    // Calculate rotation angle based on time and position
    float angle = iTime + noise(st * 0.1) * 2.0;
    angle += sin(iTime * 0.5 + st.x * 0.1) * 0.5;

    // Create rotating pattern using sine and cosine
    vec2 rot = vec2(cos(angle), sin(angle));
    vec2 rotatedUV = uv - 0.5;
    rotatedUV = vec2(rotatedUV.x * rot.y + rotatedUV.y * rot.x,
                     -rotatedUV.x * rot.x + rotatedUV.y * rot.y) + 0.5;

    // Generate color based on position and noise
    vec3 color1 = hsl2rgb(vec3(length(uv - 0.5) * 0.5, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(length(rotatedUV - 0.5) * 0.5 + 0.5, 1.0, 0.5));

    // Combine colors with noise modulation
    vec3 finalColor = mix(color1, color2, turbulent(uv * 10.0 + iTime));

    return vec4(finalColor, 1.0);
}
