vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float angle = iTime * 0.5;
    vec2 rotUv = vec2(
        uv.x * cos(angle) - uv.y * sin(angle),
        uv.x * sin(angle) + uv.y * cos(angle)
    );

    // Add some noise and turbulence
    float turb = turbulent(rotUv * 10.0);

    // Create a color based on time and position
    vec3 color = hsl(iTime + rotUv.x, 0.5, 0.5 + turb * 0.2);

    return vec4(color, 1.0);
}
