vec4 Effect2(vec2 uv) {
    // Create flowing pattern using sine waves and noise
    float time = iTime * 0.5;

    // Base frequency components
    vec2 st = uv * 8.0 + time;

    // Multiple frequencies for complex pattern
    float base = sin(st.x * 1.0) * 0.3 +
                 sin(st.y * 1.0) * 0.3 +
                 sin((st.x + st.y) * 2.0) * 0.2 +
                 sin((st.x - st.y) * 2.0) * 0.2;

    // Add noise and turbulence
    vec2 noisePos = uv * 4.0 + time;
    float turb = turbulent(noisePos);

    // Create color transitions
    float hue = (uv.x + uv.y + time * 0.5) * 3.0;
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Combine elements for final pattern
    float lines = abs(base + turb);
    lines = smoothstep(0.0, 1.0, lines);

    return vec4(lines * color, 1.0);
}
