vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = (uv - 0.5) * 12.0;

    // Add rotation based on time
    float angle = sin(iTime * 0.5) + cos(iTime);
    vec2 rotated = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    );

    // Create grid pattern with smooth color transitions
    vec3 color = hsl(
        fract(sin(rotated.x * 0.5 + iTime) * 12.0 + rotated.y * 0.7),
        0.6,
        0.5
    );

    // Add noise and turbulence for more dynamic effect
    float noiseValue = noise(vec3(st, iTime)) * 0.4;
    float turbulentValue = turbulent(rotated * 0.5) * 0.2;

    // Combine all elements
    color = mix(color, hsl2rgb(vec3(hsl2rgb(color).xy + vec2(noiseValue, turbulentValue), 1.0)), 0.3);

    return vec4(color, 1.0);
}
