vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.1;

    // Create rotating sine wave pattern
    float sinVal = sin(r * 8.0 + theta * 4.0) * 0.5 + 0.5;
    float noiseVal = turbulent(uv * 2.0 + iTime);

    // Combine patterns and add turbulence
    float finalPattern = sinVal * (1.0 - noiseVal * 0.5);

    // Create dynamic RGB shifts using HSL conversion
    vec3 color = hsl(theta * 0.5, 0.7, finalPattern * 0.6 + 0.4);

    return vec4(color, 1.0);
}
