vec4 Effect2(vec2 uv) {
    vec2 st = uv * 4.0;
    vec3 position = vec3(st.x + iTime * 0.5, st.y + iTime * 0.3, iTime * 0.1);

    float r = noise(position.xz) * 0.5 + 0.5;
    float g = noise(position.yx) * 0.5 + 0.5;
    float b = noise(position.xy + vec2(iTime)) * 0.5 + 0.5;

    float t1 = turbulent(position * 2.0);
    r += t1 * 0.3;
    g += t1 * 0.4;
    b += t1 * 0.2;

    vec3 color = hsl2rgb(vec3(r, g, b)) * (turbulent(position + vec3(iTime)) * 0.5 + 0.5);

    return vec4(color, 1.0);
}
