vec4 Effect2(vec2 uv) {
    // Create rotating geometric pattern using sine and cosine functions
    float time = iTime * 0.5;

    // Generate moving patterns with noise and turbulence
    vec3 colorBase = hsl2rgb(vec3(
        (sin(uv.x * 10.0 + time) + cos(uv.y * 10.0 + time)) / 4.0 + 0.5,
        0.7,
        0.6
    ));

    // Add turbulence for dynamic color shifts
    float turb = turbulent(vec2(uv.x * 5.0, uv.y * 5.0) + time);

    // Combine colors with turbulence and time-based shifts
    vec3 finalColor = mix(
        colorBase,
        hsl2rgb(vec3((uv.x + uv.y + time / 5.0) * 0.5, 0.8, 0.7)),
        turb * 0.4
    );

    return vec4(finalColor, 1.0);
}
