vec4 Effect2(vec2 uv) {
    // Create time-based animation
    float t = iTime * 0.5;

    // Add noise and turbulence
    vec3 noisePos = vec3(uv, t);
    float turb = turbulent(noisePos);

    // Generate color using HSL to RGB conversion with time-varying hues
    vec3 hslColor = vec3(sin(t * 0.5) + 0.5, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine elements for the final effect
    vec4 finalColor = vec4(rgbColor * (0.5 + turb * 0.5), 1.0);

    return finalColor;
}
