vec4 Effect2(vec2 uv) {
    // Create a base noise pattern with time offset
    vec2 st = uv * 5.0 + iTime;

    // Generate multiple noise layers for complexity
    float n1 = noise(st);
    float n2 = turbulent(st * 2.0);
    float n3 = turbulent(st * 4.0 + vec2(iTime));

    // Combine noise values with different frequencies
    float value = (n1 * 0.5) + (n2 * 0.3) + (n3 * 0.2);

    // Create a color transition using HSL to RGB conversion
    vec3 c = hsl2rgb(vec3(
        iTime * 0.5 + value * 0.5,   // Hue: time-based shift with noise variation
        0.7 + n2 * 0.3,             // Saturation: moderate saturation with noise
        0.5 + n1 * 0.2              // Lightness: mid-tone with noise modulation
    ));

    // Add depth by mixing colors based on different noise layers
    c = mix(c, hsl2rgb(vec3(iTime * 0.7 + n2 * 0.5, 0.6, 0.4)), abs(n3) * 0.5);

    return vec4(c, 1.0);
}
