vec4 Effect2(vec2 uv) {
    // Create rotating hexagonal grid pattern
    vec2 st = uv * 5.0; // Scale up for more tiles
    vec2 pos = floor(st + 0.5); // Get tile position

    // Convert to polar coordinates
    float angle = atan2(uv.y, uv.x) + iTime * 0.5;
    float radius = length(uv);

    // Create hexagonal grid effect
    vec2 polar = vec2(radius, angle);
    vec2 grid = floor(polar / vec2(1.0, pi/3.0));

    // Add noise and turbulence
    float noiseVal = noise(vec3(grid + iTime, 0.0)) * 0.5;
    float turbulentVal = turbulent(vec2(grid.x, grid.y + iTime));

    // Create pulsating color effect
    vec3 color = hsl(
        sin(iTime * 0.25) * 0.5 + 0.5,
        0.7 + noiseVal * 0.3,
        0.5 + turbulentVal * 0.5
    );

    // Combine effects
    vec3 finalColor = color * (1.0 - abs(noise(st * 2.0)));
    finalColor += turbulent(vec2(uv.x, uv.y + iTime)) * 0.3;

    return vec4(finalColor, 1.0);
}
