vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time-based movement
    vec2 st = uv * 10.0 + iTime;

    // Create base turbulence pattern
    float t1 = turbulent(st);

    // Add another layer of turbulence for more complexity
    float t2 = turbulent(st * 0.5 + iTime);

    // Combine turbulence values and scale for color range
    float value = (t1 + t2) * 0.5;

    // Convert to HSL color space with time-based hue shift
    vec3 hslColor = vec3(
        fract(value + iTime),   // Hue
        0.7,                   // Saturation
        0.5 + value * 0.5      // Lightness modulation
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
