vec4 Effect2(vec2 uv) {
    // Animate coordinates over time
    vec2 st = uv * 5.0 + iTime;

    // Add noise and turbulence
    float n1 = noise(st);
    float n2 = turbulent(st);

    // Create color shifts using HSL
    vec3 color = hsl(
        sin(uv.x * 4.0 + iTime) * 0.5 + 0.5,
        cos(uv.y * 4.0 - iTime) * 0.5 + 0.5,
        0.7
    );

    // Create multiple cosine layers
    float wave1 = cos(st.x * 5.0 + iTime * 2.0);
    float wave2 = cos((st.y + st.x) * 10.0 - iTime * 3.0);
    float wave3 = cos((st.x * 2.0 + st.y * 3.0) * 20.0 + iTime);

    // Combine waves and noise
    float combined = (wave1 + wave2 + wave3) * 0.3;
    combined += n1 * 0.5 + n2 * 0.2;

    // Shift RGB channels dynamically
    vec3 rgbShifts = vec3(
        sin(iTime * 0.5) * 0.5,
        cos(iTime * 0.6) * 0.5,
        sin(iTime * 0.7 + PI) * 0.5
    );

    // Final color calculation
    vec3 finalColor = hsl2rgb(vec3(
        color.r + rgbShifts.r + combined * 0.2,
        color.g + rgbShifts.g + combined * 0.2,
        color.b + rgbShifts.b + combined * 0.2
    )) * (1.0 + combined * 0.5);

    return vec4(finalColor, 1.0);
}
