vec4 Effect2(vec2 uv) {
    // Create a grid of 10x10 squares
    vec2 st = floor(uv * 10.0);

    // Calculate center of each square
    vec2 center = (floor(st) + 0.5) / 10.0;

    // Calculate distance from center
    vec2 toCenter = uv - center;

    // Rotate coordinates based on time and position
    float angle = sin(iTime + st.x + st.y) * 0.5;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rotMatrix * toCenter;

    // Create circular gradient for smooth transitions
    float dist = length(rotated);
    float circle = smoothstep(0.4, 0.5, dist);

    // Generate color using HSL with time-based hue
    vec3 color = hsl2rgb(vec3(
        (iTime + st.x + st.y) * 0.1,
        0.7,
        0.5
    ));

    // Add noise for extra detail
    float noiseVal = turbulent(uv * 10.0);
    color *= 1.0 + noiseVal * 0.3;

    return vec4(color, 1.0) * circle;
}
