vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating pattern using polar coordinates
    vec2 st = uv * 1.5; // Scale coordinates for better coverage
    float angle = atan2(st.y, st.x) + time; // Rotate with time

    // Add noise and turbulence
    float dist = length(st);
    float noiseVal = noise(vec3(uv * 0.5 + time, time)) * 0.5;

    // Create color variations using HSL
    vec3 color = hsl2rgb(vec3(
        angle * 0.1 + time * 0.2,
        0.7 + noiseVal * 0.3,
        0.5 + turbulent(vec3(uv, time)) * 0.2
    ));

    // Add distance-based modulation
    color *= sin(dist * 4.0 - time) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
