vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a flowing liquid effect using noise functions
    vec2 st = uv * 3.0 - 1.0; // Scale and translate coordinates

    // Add some movement to the pattern
    st.x += sin(time + st.y * 2.0) * 0.5;
    st.y += cos(time * 0.7 + st.x * 2.0) * 0.5;

    // Create a color gradient based on position and noise
    vec3 baseColor = hsl(0.6, 0.8, 0.4); // Dark blue

    // Add flowing liquid effect using turbulence
    float flow = turbulent(st + time);
    vec3 liquidColor = mix(baseColor, hsl(0.2, 0.7, 0.5), flow * 0.5 + 0.5);

    // Create a height-based color variation
    float height = st.y * 0.5 + 0.5;
    vec3 finalColor = mix(baseColor, liquidColor, height);

    return vec4(finalColor, 1.0);
}
