vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    float time = iTime * 0.5;

    // Create flowing noise patterns
    float noise1 = turbulent(uv * 2.0 + time) * 0.5;
    float noise2 = turbulent(uv * 4.0 + time * 0.5) * 0.5;
    float noise3 = turbulent(uv * 8.0 + time * 0.3) * 0.25;

    // Combine noise patterns with color shifts
    float hue = (noise1 + noise2 + noise3) * 0.5 + time * 0.2;
    float sat = sin(time * 0.8) * 0.5 + 0.5;
    float lig = abs(sin(noise3 * pi)) * 0.6 + 0.4;

    color = hsl2rgb(vec3(hue, sat, lig));

    return vec4(color, 1.0);
}
