vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Convert to polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime;

    // Add some turbulence
    float noiseValue = turbulent(vec2(r, theta));

    // Use HSL for color transformation
    vec3 colorHSL = hsl(noiseValue * 0.5 + 0.5, 
                       sin(theta) * 0.5 + 0.5,
                       cos(r * 4.0) * 0.5 + 0.5);

    // Convert HSL to RGB
    vec3 colorRGB = hsl2rgb(colorHSL);

    return vec4(colorRGB, 1.0);
}
