vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Add rotation based on time
    theta += iTime * 0.5;

    // Create a swirling effect by varying the radius with angle
    vec2 swirlUV = vec2(
        r * cos(theta + r * 0.5),
        r * sin(theta + r * 0.5)
    );

    // Add turbulence to create dynamic patterns
    float noiseValue = turbulent(swirlUV * 10.0) * 0.5;
    theta += noiseValue;

    // Create color gradient using HSL
    vec3 hslColor = vec3(
        theta / (2.0 * PI),   // Hue based on angle
        0.7 + noiseValue * 0.3,  // Saturation with some noise variation
        0.5 + sin(iTime) * 0.2  // Lightness varying over time
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
