vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Get cell position
    vec2 pos = floor(st);

    // Generate noise values for each cell
    float n = noise(pos + sin(iTime)*0.5);

    // Create color based on cell state and time
    vec3 baseColor = hsl(
        fract(n * 12.0 + iTime) * PI,   // Hue variation over time
        0.7,                            // Saturation
        0.5                             // Lightness
    );

    // Add turbulence for more dynamic patterns
    float turb = turbulent(st * 0.5 + vec2(iTime));

    // Combine color and turbulence
    vec3 finalColor = baseColor * (0.5 + 0.5 * turb);

    return vec4(finalColor, 1.0);
}
