vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for hexagonal pattern
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating hexagon pattern using trigonometric functions
    theta += iTime * 0.5; // Rotate over time

    // Add turbulence to create dynamic variations
    float noiseVal = turbulent(vec3(theta, r, iTime)) * 0.5 + 0.5;

    // Create pulsing color effect using HSL conversion
    vec3 hslColor = vec3(
        theta * 180.0 / PI, // Hue based on angle
        0.7,               // Saturation
        noiseVal           // Lightness varies with noise and time
    );

    // Convert to RGB and mix with another color layer
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add multiple rotating layers for more complexity
    theta += iTime * 0.7;
    r *= 0.6;
    float noiseVal2 = turbulent(vec3(theta, r, iTime)) * 0.5 + 0.5;
    vec3 hslColor2 = vec3(
        (theta + PI) * 180.0 / PI, // Offset hue for different color
        0.7,
        noiseVal2
    );
    vec3 rgbColor2 = hsl2rgb(hslColor2);

    // Combine both layers with some blending
    vec3 finalColor = (rgbColor + rgbColor2) * 0.6;

    return vec4(finalColor, 1.0);
}
