vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 q = uv - 0.5;

    // Calculate polar coordinates
    float r = length(q);
    float theta = atan2(q.y, q.x);

    // Create rotating patterns using multiple frequencies
    theta += iTime * 0.5; // Slow rotation

    // Add turbulence and color variations
    vec3 color = vec3(0.0);

    // Red channel - slow rotating wheel with radial waves
    color.r = sin(theta + iTime) * 0.5 + 0.5;
    color.r *= (1.0 - r * 2.0); // Fade to center

    // Green channel - faster rotating pattern with turbulence
    color.g = sin(theta * 3.0 + iTime * 2.0) * 0.5 + 
              sin(r * 8.0 + iTime) * 0.5;

    // Blue channel - subtle rotating waves
    color.b = cos(theta * 2.0) * 0.5 + 0.5;

    // Add turbulence effect
    float turb = turbulent(vec3(q.x*0.1, q.y*0.1, iTime)) * 0.5 + 0.5;
    color *= turb;

    return vec4(color * 1.2, 1.0);
}
