vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);
    // Create orbiting motion with time
    theta += sin(iTime * 0.5) * 0.5;

    // Add turbulence for trail effect
    vec3 color = vec3(0.0);
    float t = iTime * 0.2;

    // Create multiple layers of points
    for(float i = 0.0; i < 4.0; i++) {
        float orbitRadius = (i + 1.0) * 0.5;
        vec2 pos = vec2(cos(theta + t * i), sin(theta + t * i)) * orbitRadius;

        // Add noise and turbulence
        float noiseVal = turbulent(pos * 10.0);
        float dist = length(st - pos);

        // Create color transitions
        vec3 warmColor = hsl2rgb(vec3(0.05, 0.8, 0.5 + noiseVal * 0.2));
        vec3 coolColor = hsl2rgb(vec3(0.6, 0.8, 0.5 + noiseVal * 0.2));

        // Mix colors based on position
        color += mix(warmColor, coolColor, mod(r + iTime, 1.0)) * 
                 smoothstep(0.0, 0.1, dist) * (1.0 - noiseVal * 0.5);
    }

    return vec4(color, 1.0);
}
