vec4 Effect2(vec2 uv) {
    // Create a grid of triangles
    vec2 grid = floor(uv * 10.0 + iTime); // Scale and animate grid

    // Calculate triangle edges using cross product
    vec2 v1 = abs(grid - uv*2.0 + 0.5);
    vec2 v2 = abs(grid - uv*2.0 - 0.5);

    // Create rotating color gradients
    float angle = atan2(v1.y, v1.x) + iTime;
    float radius = length(v1);

    // HSV color with rotating gradient
    vec3 hsvColor = vec3(angle * 0.1, radius * 0.5 + 0.5, 1.0);
    vec3 rgbColor = hsv2rgb(hsvColor);

    // Add pulsing effect
    float pulse = sin(iTime) * 0.5 + 0.5;
    return vec4(rgbColor * pulse, 1.0);
}
