vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Create base sine wave patterns
    float base = sin(st.x * 4.0 + iTime) * 0.5 + 0.5;
    base += sin(st.y * 4.0 + iTime * 2.0) * 0.5;

    // Add noise and turbulence for complexity
    vec2 noiseSt = st * 0.5 + vec2(iTime);
    float n1 = noise(noiseSt) * 0.5 + 0.5;
    float t1 = turbulent(vec3(st, iTime)) * 0.5 + 0.5;

    // Combine patterns with color shifts
    vec3 color1 = hsl2rgb(vec3(n1 * 0.6 + 0.3, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(t1 * 0.6 + 0.3, 0.8, 0.5));

    // Mix layers with varying weights
    vec3 finalColor = mix(color1, color2, t1);
    finalColor *= base * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
