vec4 Effect2(vec2 uv) {
    vec2 pos = uv * 2.0 - 1.0;

    float time = iTime * 0.5;
    float angle = cos(time) * length(pos);
    angle += sin(time + length(uv)) * fract(atan2(pos.y, pos.x));

    float dist = length(pos);
    vec3 color = hsl2rgb(vec3(dist * 4.0, 1.0 - time * 0.5, 1.0));

    float noiseVal = noise(vec2(angle, dist + time));
    angle += noiseVal * 0.5;

    vec2 finalPos = vec2(cos(angle), sin(angle)) * (dist + noiseVal * 0.5);

    return vec4(color, 1.0);
}
