vec4 Effect2(vec2 uv) {
    vec3 p = vec3(uv * 0.5 - 1.0, iTime * 0.2);
    float t = turbulent(p);

    vec2 uv2 = uv + vec2(t, t * 0.5);
    float n = noise(uv2) * 4.0;

    vec3 col = hsl2rgb(vec3(n * 0.5 + 0.5, 
                          fract(sin(uv.x * 10.0 + iTime) * 6.0), 
                          fract(cos(uv.y * 8.0 + iTime) * 4.0)));

    col *= vec3(1.0, 0.8, 1.2);
    return vec4(col, 1.0);
}
