vec4 Effect2(vec2 uv) {
    vec3 baseColor = hsl2rgb(vec3(
        0.1 + noise(uv * 5.0) * 0.5,
        0.7,
        0.8
    ));

    float time = iTime * 0.5;
    vec2 st = uv;

    // Create flowing motion using turbulence
    vec2 flowMap = vec2(
        turbulent(vec2(st.x * 10.0 + time, st.y * 10.0)),
        turbulent(vec2(st.x * 10.0 - time, st.y * 10.0))
    );

    // Calculate velocity field
    vec2 vel = flowMap * 0.1;
    vec2 pos = st + vel;

    // Create surface normal based on velocity
    vec3 norm = vec3(vel.x, vel.y, 0.5);
    norm = normalize(norm);

    // Reflect light direction using normal vector
    vec3 lightDir = vec3(1.0, 1.0, 1.0);
    lightDir = normalize(lightDir);
    vec3 reflectDir = reflect(-lightDir, norm);

    // Calculate specular highlight
    float spec = pow(max(dot(norm, reflectDir), 0.0), 32.0);

    // Mix colors and apply reflections
    baseColor *= (0.7 + spec * 0.3);
    return vec4(baseColor, 1.0);
}
