vec4 Effect2(vec2 uv) {
    float t = iTime * 0.1;

    vec2 st = uv + vec2(t, sin(t));

    // Generate noise pattern
    float n1 = noise(st);
    float n2 = noise(uv + t + cos(t));

    // Combine noise patterns with smooth transitions
    float d = smoothstep(0.0, 1.0, sin(n1 * pi)) * 
              smoothstep(0.0, 1.0, sin(n2 * pi));

    // Add depth and layers using multiple noise functions
    n1 = noise(vec2(uv.x + t, uv.y + cos(t)));
    d += smoothstep(0.0, 1.0, sin(n1 * pi)) * 0.5;

    // Create glowing effect with alpha based on distance to edge
    float a = length(d - 0.3) * 2.0;
    a = smoothstep(0.0, 1.0, a);

    // Generate shifting colors using HSL
    vec3 color = hsl2rgb(vec3(sin(t) * 0.5 + 0.5, 
                              cos(t) * 0.5 + 0.5,
                              sin(iTime * 0.2) * 0.3 + 0.7));

    // Apply glow intensity and mix with background
    color *= vec3(1.0, 1.0, 1.0) * a;

    return vec4(color, a);
}
