vec4 Effect2(vec2 uv) {
    // Flow parameters
    float s = 0.1;
    float speed = 0.5;
    float scale = 0.3;

    // Create flowing pattern
    vec2 flow = vec2(
        sin(uv.y * scale + iTime * speed),
        cos(uv.x * scale + iTime * speed)
    );

    // Add noise for texture
    float noiseVal = noise(uv * 5.0);

    // Combine flow and noise for color variation
    vec3 hsv = vec3(
        (flow.x + flow.y) * 0.5 + 0.5,  // Hue variation
        0.8,                              // Saturation
        0.7 + noiseVal * 0.2             // Value with noise
    );

    // Convert HSV to RGB and add glow effect
    vec3 color = hsv2rgb(hsv);
    float glow = smoothstep(0.5, 1.0, length(uv));
    color += vec3(glow) * 0.2;

    return vec4(color, 1.0);
}
