vec4 Effect2(vec2 uv) {
    vec3 color;
    vec2 st = uv * 10.0;

    // Create a grid pattern with time-based shifting
    float cellSize = 1.0;
    vec2 cells = floor(st + iTime * 5.0);

    // Generate noise-based variation
    float noiseValue = noise(cells * vec2(37.7, 43.3));

    // Calculate HSL values with time influence
    float h = iTime * 10.0;
    float s = 0.8 + sin(h) * 0.2;
    float l = 0.5 + noiseValue * 0.3;

    // Convert HSL to RGB
    color = hsv2rgb(vec3(h, s, l));

    // Add turbulence effect for shifting patterns
    vec2 pos = uv + turbulent(uv * 5.0) * 0.1;

    // Apply the final color transformation
    return vec4(color, 1.0);
}
